import React from 'react';
import { AbsoluteFill } from 'remotion';
import { TitleCard } from '../components';

export const Scene1_DNAIntro: React.FC = () => {
  return (
    <AbsoluteFill>
      <TitleCard title="DNA 双链结构" subtitle="生物学基础" />
    </AbsoluteFill>
  );
};

export default Scene1_DNAIntro;

import React from 'react';
import { AbsoluteFill } from 'remotion';
import { Subtitle, AISpeaker, FullScreen } from '../components';

export const Scene2_DNAStructure: React.FC = () => {
  return (
    <FullScreen backgroundColor="#1e293b">
      <AISpeaker name="AI 老师" position="right" speaking={true} />
      <div style={{ padding: 100, color: 'white' }}>
        <h2 style={{ fontSize: 64, marginBottom: 40 }}>双螺旋结构</h2>
        <p style={{ fontSize: 36, lineHeight: 1.8 }}>
          DNA 由两条反向平行的核苷酸链组成，
          通过氢键连接形成稳定的双螺旋结构。
        </p>
      </div>
      <Subtitle text="双螺旋：生命的密码" position="bottom" />
    </FullScreen>
  );
};

export default Scene2_DNAStructure;
